mapper.select:
SELECT err.title as "title" , COUNT(*) as "value" FROM t_device de , t_errors err WHERE de.deviceId = err.deviceId AND de.deviceId = #{deviceId}
AND err.occurDate BETWEEN #{startDay} AND #{endDay}
GROUP BY err.title order by COUNT(*) desc LIMIT 10;

SELECT CONCAT(DATE_FORMAT(err.occurDate,'%d'),'일') as "title" , COUNT(*) as "value" FROM t_device de , t_errors err WHERE de.deviceId = err.deviceId AND de.deviceId = #{deviceId}
AND err.occurDate BETWEEN #{startDay} AND #{endDay}
GROUP BY CONCAT(DATE_FORMAT(err.occurDate,'%d'),'일') ORDER BY COUNT(*) desc
